import React, { Component } from "react";
import "./App.css";
import DadataSuggestions from 'react-dadata-suggestions';
import "react-dadata-suggestions/dist/styles.css";

const dadataToken = '9357f46cef73bd355912567b08e7ecd06f531745';

// const PLACES = [
//   { name: "Москва", zip: "105005" },
//   { name: "Санкт-Петербург", zip: "187015" },
//   { name: "Сочи", zip: "354000" },
//   { name: "Пятигорск", zip: "357361" }
// ]

class WeatherDisplay extends Component {
  constructor() {
    super();
    this.state = {
      weatherData: null
    };
  }
  componentDidMount() {
    const geoname_id = this.state.suggestion;
    const URL = "api.openweathermap.org/data/2.5/weather?id=" + geoname_id + "&appid=2fe9657dc864de269a18a030c1815838";
    fetch(URL).then(res => res.json()).then(json => {
      this.setState({ weatherData: json });
    });
    console.log('вот тут должен быть объект с подсказками ' + geoname_id)
  }

  render() {
    const weatherData = this.state.weatherData;

    if (!weatherData) return <div>Loading...</div>;
    const weather = weatherData.weather[0];
    const iconUrl = "http://openweathermap.org/img/w/" + weather.icon + ".png";

    return (
      <div>
        <h1>
          {weather.main} in {weatherData.name}
          <img src={iconUrl} alt={weatherData.description} />
        </h1>
        <p>Current: {weatherData.main.temp}°</p>
        <p>High: {weatherData.main.temp_max}°</p>
        <p>Low: {weatherData.main.temp_min}°</p>
        <p>Wind Speed: {weatherData.wind.speed} mi/hr</p>
      </div>
    );
  }
}


class App extends Component {

  constructor() {
   super();
   this.state = {
     suggestion: []
   };
 }

 state = {
   suggestion: []
 }

  render() {

    return (
      <div className="weather-body">
        <DadataSuggestions
          className="dadata-body"
          token={ dadataToken }
          onSelect={ (suggestion) => console.log(suggestion.data.geoname_id) }
        />
        <div>вот еще {this.state.suggestion}</div>
        <WeatherDisplay/>
      </div>
    );
  }
}

export default App;
